j$ = jQuery.noConflict();
j$.blockUI({
     css: {
          border: 'none',
          padding: '15px',
          backgroundColor: '#000',
          '-webkit-border-radius': '10px',
          '-moz-border-radius': '10px',
          opacity: .5,
          color: '#fff'
     },
     message : 'Creating folders ... this may take a moment.',
     onBlock: function() { 
          try {
               var runSearchStatus = sforce.apex.execute("BoxSearchAndCreateMissingFolders","createFolderHierarchyIHService",
                            {Id:'{!Property_Opportunity__c.Id}', stage:'Pipeline'});

                alert(runSearchStatus);
           }
           catch(e) {
                alert(e);
           }
      }
});
j$.unblockUI();
