var records = {!GETRECORDIDS($ObjectType.Property__c)};
var strIDs='';
if (records[0] == null)
{
alert('Please select a record');
}
else
{
for (var n=0; n<records.length; n++)
{
if(strIDs == '')
strIDs = records[n];
else
strIDs = strIDs + "," + records[n] ;
}
window.location.href = '/apex/MassManager__MassUpdate?selectedRecords='+strIDs + '&selectedObject=property__c&retURL='+ window.location.href;
}
